import React, { Component } from "react";
import ReactDOM, { createRoot } from "react-dom/client";
import "./index.css";

import Footer from "./component/footer";
import NewTaskForm from "./component/new-task-form";
import TaskList from "./component/task-list";

export default class App extends Component {
  state = {
    todoData: [
      { label: "Completed task", editing: false, status: "completed", id: 1 },
      { label: "Editing task", editing: true, status: "editing", id: 2 },
      { label: "Active task", editing: false, id: 3 },
    ],
  };

  deleteTask = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const newArray = todoData.filter((el, i) => {
        if (i !== idx) {
          return true;
        }
      });

      return {
        todoData: newArray,
      };
    });
  };
  onToggleCompleted = (id) => {};

  onToggleEditing = (id) => {};

  render() {
    return (
      <section className="todoapp">
        <header className="header">
          <h1>todos</h1>
          <NewTaskForm />
        </header>
        <section className="main">
          <TaskList
            todos={this.state.todoData}
            onDeleted={this.deleteTask}
            onSave={this.save}
          />
          <Footer />
        </section>
      </section>
    );
  }
}

const root = createRoot(document.getElementById("root"));
root.render(<App />);
