import React, { Component } from "react";
import ReactDOM, { createRoot } from "react-dom/client";
import "./index.css";

import Footer from "./component/footer";
import NewTaskForm from "./component/new-task-form";
import TaskList from "./component/task-list";

export default class App extends Component {
  maxId = 100;
  state = {
    todoData: [
      this.createDataItem("Completed task"),
      this.createDataItem("Editing task"),
      this.createDataItem("Active task"),
    ],
  };

  createDataItem(text) {
    return {
      label: text,
      editing: false,
      completed: false,
      id: this.maxId++,
    };
  }

  deleteTask = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const newArray = todoData.filter((el, i) => {
        if (i !== idx) {
          return true;
        }
      });

      return {
        todoData: newArray,
      };
    });
  };
  onToggleCompleted = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const oldItem = todoData[idx];
      const newItem = { ...oldItem, completed: !oldItem.completed };
      const newArray = [
        ...todoData.slice(0, idx),
        newItem,
        ...todoData.slice(idx + 1),
      ];

      return {
        todoData: newArray,
      };
    });
  };

  onToggleEditing = (id) => {
    console.log("toggle editing");
  };

  render() {
    return (
      <section className="todoapp">
        <header className="header">
          <h1>todos</h1>
          <NewTaskForm />
        </header>
        <section className="main">
          <TaskList
            todos={this.state.todoData}
            onDeleted={this.deleteTask}
            onSave={this.save}
            onToggleCompleted={this.onToggleCompleted}
            onToggleEditing={this.onToggleEditing}
          />
          <Footer />
        </section>
      </section>
    );
  }
}

const root = createRoot(document.getElementById("root"));
root.render(<App />);
